mapper.select:
SELECT a.*,p.groupno,p.score FROM participate p
LEFT JOIN athlete a
ON p.ath_id=a.id
WHERE p.comp_id=#{param1}
AND a.teamid = #{param2}
ORDER BY p.score DESC

SELECT SUM(p.score) FROM participate p
LEFT JOIN competition c
ON p.comp_id=c.id
WHERE p.ath_id=#{param1}
AND c.type = #{param2}

SELECT SUM(p.score) FROM participate p
LEFT JOIN athlete a
ON p.ath_id=a.id
WHERE p.comp_id=#{param1}
GROUP BY a.teamid
HAVING a.teamid = #{param2}

mapper.delete:
DELETE FROM participate
WHERE ath_id=#{param1}
AND comp_id=#{param2}

mapper.update:
UPDATE participate
SET groupno = #{param3}
WHERE ath_id = #{param1}
AND comp_id = #{param2}

UPDATE participate
SET score = #{param3}
WHERE ath_id = #{param1}
AND comp_id = #{param2}
