mapper.select:
SELECT a.id athid, a.`name`,a.no,t.id t_id,t.score,t.isValid
FROM tempscore t
LEFT JOIN athlete a
ON t.ath_id=a.id
WHERE t.id=
      (SELECT MAX(id) max_tid
       FROM tempscore
       WHERE ath_id = #{param2}
         AND judge_id =#{param1}
      )

SELECT id,score
FROM tempscore
WHERE id=
      (SELECT MAX(t.id)
       FROM tempscore t
                LEFT JOIN judge j
                          ON t.judge_id=j.id
       WHERE j.comp_id=#{param1}
        AND j.ref_id=#{param2}
        AND t.ath_id = #{param3}
      )

SELECT id,score,isValid
FROM tempscore
WHERE ath_id= #{param2}
AND judge_id= #{param1}

SELECT t.score,t.isValid
FROM tempscore t
         LEFT JOIN judge j
                   ON t.judge_id=j.id
WHERE j.comp_id=#{param1}
  AND j.ref_id=#{param2}
  AND t.ath_id =#{param3}

mapper.update:
UPDATE tempscore
SET valid = #{param2}
WHERE id = #{param1}
